mapper.select:
		
        SELECT resNum, resDate, resTime, borName, resRegdate, borNum, resCount, memId 
          FROM (
	                SELECT resNum, resDate, resTime, borName, resRegdate, borNum, resCount, memId FROM reserve 
	                WHERE resNum > #{resNum}  
	                ORDER BY resNum ASC 
               ) WHERE ROWNUM = 1

		
