mapper.select:
		
        SELECT bonNum, bonTitle, bonContent, bonReadcount, bonRegdate 
          FROM (
	                SELECT bonNum, bonTitle, bonContent, bonReadcount, bonRegdate FROM notice 
	                WHERE bonNum > #{bonNum}  
	                ORDER BY bonNum ASC 
               ) WHERE ROWNUM = 1

		
        SELECT bonNum, bonTitle, bonContent, bonReadcount, bonRegdate 
          FROM (
	                SELECT bonNum, bonTitle, bonContent, bonReadcount, bonRegdate FROM notice 
	                WHERE bonNum < #{bonNum}  
	                ORDER BY bonNum ASC 
               ) WHERE ROWNUM = 1
